Method.Implementation:
// Allow any user access to the documentation.
do ..addAccessRule("index", "*", "*")

quit $$$OK

set:$get(parameters)="" parameters = ##class(EasyCSP.Core.HashArray).%New()
set model = ##class(EasyCSP.Core.Component).objectFactory("Documentation", "model") if '..instanceOf( model, "Documentation") { throw ##class(EasyCSP.Core.Exception).%New($classname($this),"I10","","Can not obtain an instance of model Documentation") quit 0 }
do parameters.set("model", model)
set queryId = %EasyCSPApp.getCGI("queryId") if queryId = "" { do parameters.set("select","ClassId,CacheVersion,ClassType,Description") set queryId = $classmethod(model.%ClassName(1), "search", parameters) }
do parameters.set("allowDownload", 1) do parameters.set("downloadName", "Documentation.csv") do parameters.set("noCreate", 1) do parameters.set(%EasyCSPApp.getRequestData("pageNumber")) do parameters.set(%EasyCSPApp.getRequestData("pageSize")) do parameters.set("queryId", queryId)
// Show only the View button. The documentation shouldn't be edited by hand. set buttons = ##class(EasyCSP.Core.Array).%New() do buttons.append(##class(EasyCSP.Web.Widget.ButtonColumn).defineButton("viewmethods", "View Methods", "list-green-bullet.png")) do buttons.append(##class(EasyCSP.Web.Widget.ButtonColumn).defineButton("viewproperties", "View Properties", "list-green-check.png")) set buttonSave = ##class(EasyCSP.Rendering.json).toString(##class(EasyCSP.Rendering.json).toJSON("buttons", buttons))
set ^queryOut(queryId, "buttons") = buttonSave
quit ..render("QueryResult", parameters)
quit 1

set:$get(parameters)="" parameters = ##class(EasyCSP.Core.HashArray).%New()

// Which class record?
set id=""
set:$get(parameters)["HashArray" id = parameters.get("id")
set:id="" id = %EasyCSPApp.getRequestData("id")

// Retrieve that one
set viewArray = ##class(EasyCSP.Model.Models.Documentation).methods(id)

// Pass data to a view through a Framework parameter structure.
do parameters.set("array", viewArray)
do parameters.set("pageTitle", id_" Methods")

// ... and render the view in its layout
quit ..render("ArrayView", parameters)

set:$get(parameters)="" parameters = ##class(EasyCSP.Core.HashArray).%New()

// Which group record?
set id=""
set:$get(parameters)["HashArray" id = parameters.get("id")
set:id="" id = %EasyCSPApp.getRequestData("id")

// Retrieve that one
set viewArray = ##class(EasyCSP.Model.Models.Documentation).properties(id)

// Pass data to a view through a Framework parameter structure.
do parameters.set("array", viewArray)
do parameters.set("pageTitle", id_" Properties")

// ... and render the view in its layout
quit ..render("ArrayView", parameters)
